function createCircularText(t){let r=document.getElementById(t);t=(r.textContent.trim()+" ").split("");let a=360/t.length;r.textContent="",t.forEach((t,e)=>{var n=document.createElement("span");n.textContent=t,n.style.transform=`rotate(${e*a}deg)`,r.appendChild(n)})}document.addEventListener("DOMContentLoaded",function(){createCircularText("circular-text")});